mapper.select:
SELECT id, userId, password, nickName, createTime, isWithDraw, status
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET password = #{password},
    nickName = #{nickName},
    isWithDraw = #{isWithDraw},
    status = #{status}
WHERE id = #{id}

mapper.select:
SELECT COUNT(id)
FROM user
WHERE userId = #{userId}

mapper.update:
UPDATE user
SET passWord = #{password}
WHERE userId = #{userId}
